changeSet.sql:
CREATE TABLE roles (
    id BIGSERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL UNIQUE
);

ALTER TABLE public.users DROP CONSTRAINT IF EXISTS chk_user_status;
ALTER TABLE public.users ADD CONSTRAINT chk_user_status
    CHECK (status IN ('ACTIVE','INACTIVE','LOCKED','PENDING_EMAIL_VERIFICATION'));
ALTER TABLE public.users ALTER COLUMN status SET DEFAULT 'PENDING_EMAIL_VERIFICATION';
